package test;

public class Calculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=20, num2=10, sum=0;
		sum = num1 + num2;
		System.out.println("Sum of 2 number is " +sum);
		int sub = num1-num2;
		System.out.println("Sub of 2 number is "+sub);
		int mul = num1*num2;
		System.out.println("Mul of two numbers "+mul);
		float div = num1/num2;
		System.out.println("Division of 2 number is "+div);
		
	}

}
